        // 3. Chord Diagrms
        y = this.layoutAndRenderChordDiagrams(y, -1);
        //
        // 4. One result per StaveGroup
        y = this.layoutAndRenderScore(y);
        y = this.layoutAndRenderAnnotation(y);
        this.height = y + this._pagePadding[3];
